con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

// Practice
log.info "Hello, This is my first project"
log.info context.expand('${#TestCase#name}')
log.info context.expand('${#TestCase#age}')
log.info testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("id")
log.info testRunner.testCase.testSuite.testCases["getEmployeeDetails TestCase"].testSteps["getEmployeeDetails"].getPropertyValue("Request")
testRunner.testCase.testSuite.testCases["addEmployee TestCase"].setPropertyValue("id", "4591")


// Test cases for add, get and delete employee details

// Add employee details
def addReq = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].getPropertyValue("Request")
def name = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("name")
def id = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("id")
def dept = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("dept")
def age = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("age")
def xmlAdd = new XmlHolder(addReq)
xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",dept)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)
def addXmlReq = xmlAdd.getXml()
testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].setPropertyValue("Request",addXmlReq)

def addTestStep = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"]
def contextAddEmployee = new WsdlTestRunContext(addTestStep)
addTestStep.run(testRunner,contextAddEmployee)


// Get employee details
def getReq = testRunner.testCase.testSuite.testCases["getEmployeeDetails TestCase"].testSteps["getEmployeeDetails"].getPropertyValue("Request")
def getEmpXml = new XmlHolder(getReq)
getEmpXml.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)
def newEmpXml = getEmpXml.getXml()
testRunner.testCase.testSuite.testCases["getEmployeeDetails TestCase"].testSteps["getEmployeeDetails"].setPropertyValue("Request",newEmpXml)

def getTestStep = testRunner.testCase.testSuite.testCases["getEmployeeDetails TestCase"].testSteps["getEmployeeDetails"]
def contextGetEmployee = new WsdlTestRunContext(getTestStep)
getTestStep.run(testRunner,contextGetEmployee)


// Validation
def getRes = testRunner.testCase.testSuite.testCases["getEmployeeDetails TestCase"].testSteps["getEmployeeDetails"].getPropertyValue("Response")
def getEmpRes = new XmlHolder(getRes)
def getEmpResName = getEmpRes.getNodeValue("//ns:getEmployeeDetailsResponse/ns:return/ns:name")
log.info getEmpResName
log.info name
assert getEmpResName == name


// Delete employee details
def delReq = testRunner.testCase.testSuite.testCases["deleteEmployee TestCase"].testSteps["deleteEmployee"].getPropertyValue("Request")
def delXml = new XmlHolder(delReq)
delXml.setNodeValue("//typ:deleteEmployee/typ:employeeName",name)
def delEmpXml = delXml.getXml()
testRunner.testCase.testSuite.testCases["deleteEmployee TestCase"].testSteps["deleteEmployee"].setPropertyValue("Request",delEmpXml)

def delTestStep = testRunner.testCase.testSuite.testCases["deleteEmployee TestCase"].testSteps["deleteEmployee"]
def contextDelEmp = new WsdlTestRunContext(delTestStep)
delTestStep.run(testRunner,contextDelEmp)
